        <?php
          $members = get_posts( array(
              'post_type' => 'team_member',
              'orderby' => 'post_date',
              'order' => 'ASC'
              ) );

          foreach( $members as $member ) {
            $data_name = get_post_meta( $member->ID, 'data_name', true );
            $tel = get_post_meta( $member->ID, 'tel', true );
            $email = get_post_meta( $member->ID, 'email', true );

            $html = '';
            $html .= '<div class="about-item" data-name="' . get_post_meta( $member->ID, 'data_name', true ) . '">';
            $html .= '<div class="info">';
            $html .= '<h3>' . $member->post_title  .'</h3>';
            $html .= '<p>' . $member->post_excerpt . '</p>';
            $html .= '</div>';
            if(!empty($tel) || !empty($email)) {
              $html .= '<div class="contact-us">';
              $html .= '<div class="btn btn--white contact-artem">Contacts</div>';
              $html .= '<div class="contacts">';
              $html .= '<a href="tel:' . $tel .'">' . $tel . '</a>';
              $html .= '<a href="mailto:' . $email . '">' . $email . '</a>';
              $html .= '</div>';
              $html .= '</div>';
            }
            $html .= '</div>';
            echo $html ;
          }
        ?>
        </div>
    </div>
