<section class="portfolio" id="portfolio">
  <h2>Portfolio</h2>
   <ul class="portfolio__list">
    <?php
    $args = array(
      'post_type' => 'portfolio',
      'orderby' => 'post_date',
      'order' => 'ASC'
      );
      $posts = get_posts( $args );

      foreach( $posts as $post ) {
        setup_postdata($post->ID);
        $color = get_post_meta( $post->ID, 'portfolio_color', true );
        $html = '';
        $html .= '<li class="portfolio__item parallax" style="background: linear-gradient(rgba(' . $color .', 0.95), rgba(' . $color .', 0.95)), url('. get_the_post_thumbnail_url( $post->ID, 'full' ) .');">';
        $html .= '<div class="portfolio__text">';
        $html .= get_post_meta( $post->ID, 'portfolio_logo', true );
        $html .= '<p>' . get_the_excerpt($post->ID) . '</p>';
        $html .= '</div>';
        $html .= '<figure class="portfolio__img">';
        $html .= '<img src="' .get_the_post_thumbnail_url( $post->ID, 'full' ) . '" alt="">';
        $html .= ' </figure>';
        $html .= '</li>';

        echo $html;
      }

      wp_reset_postdata();

